import Task from "./Task";
import React from "react";
import { Grid, Row, Col } from "react-bootstrap";
function Tasks({ tasks, onTglStatus }) {
  return (
      <Grid>
      <Row className="show-grid">
        {tasks.map((task) => (
          <React.Fragment>

          <Col key={task.id}>
            <Task task={task} onTglStatus={onTglStatus} />
          </Col>
          <Col key={task.id}>
            <Task task={task} onTglStatus={onTglStatus} />
          </Col>
          <Col key={task.id}>
            <Task task={task} onTglStatus={onTglStatus} />
          </Col>


          </React.Fragment>
        ))}
      </Row>
      </Grid>

  );
}

export default Tasks;
