import Task from "./Task";
import React from "react";
import { Container, Row, Col } from "react-bootstrap";
function Tasks({ tasks, onTglStatus }) {
  return (
      <Container>
      <Row>
        {tasks.map((task) => (
          <React.Fragment>
          if(task.col == 1){
            <Col key={task.id}>
              <Task task={task} onTglStatus={onTglStatus} />
            </Col>
          }
          else if(task.col == 2){
            <Col key={task.id}>
              <Task task={task} onTglStatus={onTglStatus} />
            </Col>
          }
          else{
            <Col key={task.id}>
              <Task task={task} onTglStatus={onTglStatus} />
            </Col>
          }
          
          </React.Fragment>
        ))}
       </Row>
      </Container>

  );
}

export default Tasks;
