import Task from "./Task";
function Tasks({ tasks, onTglStatus }) {
  return (
    <div className="card">
      <div className="row">
        {tasks.map((task) => (
          <React.Fragment>


          <div className="col-8" key={task.id}>
            <Task task={task} onTglStatus={onTglStatus} />
          </div>
          <div className="col-8" key={task.id}>
            <Task task={task} onTglStatus={onTglStatus} />
          </div>
          <div className="col-8" key={task.id}>
            <Task task={task} onTglStatus={onTglStatus} />
          </div>
          </React.Fragment>
        ))}
      </div>
    </div>
  );
}

export default Tasks;
